import react, { useState } from "react";
import "./ExpenseForm.css";

const ExpenseForm = () => {

    const sumbitHandler = (event) => {
        event.prevenDefault();

        const expenseData = {
            title : setEnteredTitle,
            amount : setEnteredAmount,
            data : new Date(setEnteredDate)
        }

        setEnteredAmount(enteredAmount);
    }

    const [enteredTitle, setEnteredTitle] = useState("");
    const [enteredAmount, setEnteredAmount] = useState("");
    const [enteredDate, setEnteredDate] = useState(" ");

    const titleChangeHandler = (event) => {
        setEnteredTitle(event.targer.value);
    }

    const amountChangeHandler = (event) => {
        setEnteredAmount(event.target.value);
    }

    const dateChangeHandler = (event) => {
        setEnteredDate(event.target.value);
    }
    return (
        <form onSubmit={sumbitHandler}>
            <div className="new-expense__controls">
                <div className="new-expense__control">
                    <label>Title</label>
                    <input type="text" onChange={titleChangeHandler}></input>
                </div>
                <div className="new-expense__control">
                    <label>Amount</label>
                    <input type="number" min="0.01" step="0.01" onChange={amountChangeHandler}></input>
                </div>
                <div className="new-expense__control">
                    <label>Date</label>
                    <input type="date" min="2019-01-01" max="2022-12-31" onChange={dateChangeHandler}></input>
                </div>
            </div>
            <div className="new-expense__actions">
                <button type="submit">Add Expenses</button>
            </div>
        </form>
    );
}

export default ExpenseForm;
